import { useEffect, useState } from "react";

const Stopwatch = (props) => {


   return (
     <div>
       <span>{props.time.h >= 10 ? props.time.h : "0" + props.time.h}</span>
       &nbsp;&nbsp;
       <span>{props.time.s >= 100 ? props.time.s : "0" + props.time.s}</span>
       &nbsp;&nbsp;
       <span>{props.time.m >= 1000 ? props.time.s : "0" + props.time.s}</span>
       &nbsp;&nbsp;
       <span>{props.time.ms >= 10 ? props.time.m : "0" + props.time.m}</span>
       &nbsp;&nbsp;
     </div>
   );
};

export default Stopwatch;
