import classes from "./Button.module.scss";

const Button = (props) => {
 return (
   <div className={classes["stopwatch"]}>
     <div className={classes["stopwatch-btn"]}>
       {!props.start && props.time === 0 && (
         <button className={classes["stopwatch-btn-red"]} onClick={propshandleStart}>
           Start
         </button>
       )}

       {start && (
         <button className={classes["stopwatch-btn-yel"]} onClick={handleStop}>
           Stop
         </button>
       )}

       {!start && time !== 0 && (
         <button
           className={classes["stopwatch-btn-gre"]}
           onClick={handleResume}
         >
           Resume
         </button>
       )}
       {!start && time > 0 && (
         <button
           className={classes["stopwatch-btn-green"]}
           onClick={handleReset}
         >
           Reset
         </button>
       )}
     </div>
   </div>
 );
};

export default Button;
