import { useState, useEffect } from "react";
import Button from "./Components/Button/Button";
import Stopwatch from "./Components/Timer/Stopwatch";

import classes from "./App.module.scss";

const App = () => {
  const [time, setTime] = useState(0);
  const [start, setStart] = useState(false);

  useEffect(() => {
    let interval = null;
    if (start) {
      interval = setInterval(() => setTime((prevTime) => prevTime + 10), 10);
    } else {
      clearInterval(interval);
    }

    return () => {
      clearInterval(interval);
    };
  }, [start]);
  const handleStart = () => {
    setStart(true);
  };
  const handleStop = () => {
    setStart(false);
  };
  const handleResume = () => {
    setStart(true);
  };
  const handleReset = () => {
    setTime(0);
  };
  return (
    <div className={classes["main-section"]}>
      <div className={classes["clock-holder"]}>
        <div className={classes.stopwatch}>
          <h1>STOPWATCH</h1>
          <div>
            <span>{("0" + Math.floor((time / 60000) % 60)).slice(-2)}</span>
            &nbsp;:&nbsp;
            <span>{("0" + Math.floor((time / 1000) % 60)).slice(-2)}</span>
            &nbsp;:&nbsp;
            <span>{("0" + Math.floor((time / 100) % 60)).slice(-2)}</span>
            &nbsp;:&nbsp;
            <span>{("0" + Math.floor((time / 10) % 100)).slice(-2)}</span>
            &nbsp;:&nbsp;
          </div>
        </div>
        <div className={classes["stopwatch"]}>
          <div className={classes["stopwatch-btn"]}>
            <button
              className={classes["stopwatch-btn-red"]}
              onClick={handleStart}
            >
              Start
            </button>
            <button
              className={classes["stopwatch-btn-yel"]}
              onClick={handleStop}
            >
              Stop
            </button>
            <button
              className={classes["stopwatch-btn-gre"]}
              onClick={handleResume}
            >
              Resume
            </button>
            <button
              className={classes["stopwatch-btn-green"]}
              onClick={handleReset}
            >
              Reset
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
